package Lec_01;

public class Maximum_Of_three_Number {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int a=8;
		 int b=7;
		 int c=78;
		 if(a>b && a>c) {
			 System.out.println(a);
		 }
		 else if(b>c && b>a) {
			 System.out.println(b);
		 }
		 else {
			 System.out.println(c);
		 }

	}

}
